// helper (preview modal)
(function(){ document.addEventListener("click", function(e){ if(e.target && e.target.classList && e.target.classList.contains("open-preview")){ e.preventDefault(); var src=e.target.dataset.src||e.target.href; var m=document.createElement("div"); m.style.cssText="position:fixed;left:0;top:0;width:100%;height:100%;background:rgba(0,0,0,.85);display:flex;align-items:center;justify-content:center;z-index:9999"; m.innerHTML="<img src='"+src+"' style=\"max-width:95%;max-height:95%;border-radius:6px\">"; m.addEventListener("click", function(){m.remove()}); document.body.appendChild(m); } }, false); })();
